def safe_json_parse(json_string):
    try:
        data = json.loads(json_string)
        return {"ok": True, "data": data}
    except json.JSONDecodeError as e:
        return {
            "ok": False,
            "error": str(e.msg),
            "position": e.pos
        }

print(safe_json_parse('{"name": "Alice"}'))
print(safe_json_parse('{"name": invalid}'))
print(safe_json_parse('{missing: "quotes"}'))

def factorial(n):
    if n <= 1:
        return 1
    return n * factorial(n - 1)

