def factorial(n):
    if n <= 1:
        return 1
    return n * factorial(n - 1)

print(factorial(4))

def sum_list(lst):
    if len(lst) == 0:
        return 0
    return lst[0] + sum_list(lst[1:])

print(sum_list([2, 3, 5]))

def mystery(n):
    if n == 0:
        return 0
    return n % 10 + mystery(n // 10)

print(mystery(123))

def power(base, exp):
    if exp == 0:
        return 1
    return base * power(base, exp - 1)

print(power(2, 4))

def reverse(s):
    if len(s) <= 1:
        return s
    return s[-1] + reverse(s[:-1])

print(reverse("abc"))

def count(n):
    if n <= 0:
        return 0
    return 1 + count(n // 2)

print(count(8))
