print(calculate_bmi(85, 1.80))

def compound_interest(principal, rate, n, years):
    amount = principal * (1 + rate/n) ** (n * years)
    return round(amount, 2)

print(compound_interest(1000, 0.05, 12, 10))
print(compound_interest(5000, 0.03, 4, 5))

def classify_length(text):
    length = len(text)
    if length < 5:
        return "short"
    elif length <= 10:
        return "medium"
    else:
        return "long"

