def validate_type(value, expected_type):
    type_map = {
        "int": int,
        "float": float,
        "str": str,
        "bool": bool
    }
    if expected_type not in type_map:
        return False
    if expected_type == "int" and isinstance(value, bool):
        return False
    return isinstance(value, type_map[expected_type])

print(validate_type(42, "int"))
print(validate_type("hello", "str"))
print(validate_type(3.14, "int"))

def parse_coordinates(coord_string):
    try:
        parts = coord_string.split(",")
        if len(parts) != 2:
            return None
        x = float(parts[0])
        y = float(parts[1])
        return (x, y)
    except ValueError:
        return None

print(parse_coordinates("10,20"))
print(parse_coordinates("3.5,7.2"))
print(parse_coordinates("invalid"))
