def deep_get(d, keys):
    current = d
    for key in keys:
        if isinstance(current, dict) and key in current:
            current = current[key]
        else:
            return None
    return current

data = {
    "user": {
        "profile": {
            "name": "Alice",
            "age": 30
        }
    }
}
print(deep_get(data, ["user", "profile", "name"]))
print(deep_get(data, ["user", "settings", "theme"]))

def remove_duplicates(items):
    seen = []
    for item in items:
        if item not in seen:
            seen.append(item)
    return seen

print(remove_duplicates([1, 2, 2, 3, 1, 4]))
print(remove_duplicates(["a", "b", "a", "c"]))

def flatten(nested):
    result = []
    for sublist in nested:
        for item in sublist:
            result.append(item)
    return result

print(flatten([[1, 2], [3, 4], [5]]))
print(flatten([["a"], ["b", "c"]]))

def lists_to_dict(keys, values):
    result = {}
    for i in range(len(keys)):
        result[keys[i]] = values[i]
    return result

print(lists_to_dict(["a", "b", "c"], [1, 2, 3]))
print(lists_to_dict(["name", "age"], ["Alice", 30]))

def find_common(list1, list2):
    common = []
    for item in list1:
        if item in list2 and item not in common:
            common.append(item)
    return common

print(find_common([1, 2, 3, 4], [3, 4, 5, 6]))
print(find_common(["a", "b", "c"], ["b", "d"]))

def sort_by_value(d):
    items = list(d.items())
    sorted_items = sorted(items, key=lambda x: x[1], reverse=True)
    return sorted_items

scores = {"Alice": 85, "Bob": 92, "Charlie": 78}
print(sort_by_value(scores))

def divisible_by_2_and_3(numbers):
    return [n for n in numbers if n % 2 == 0 and n % 3 == 0]

print(divisible_by_2_and_3([1, 2, 3, 6, 12, 15, 18, 24]))
