solution:
class TodoList:
    def __init__(self):
        self._tasks = []
        self._done = set()

    def add(self, task):
        self._tasks.append(task)

    def done(self, task):
        self._done.add(task)

    def pending(self):
        return [t for t in self._tasks if t not in self._done]

