solution:
#include <stdio.h>

void findMinMax(int *arr, int size, int *min, int *max) {
    *min = *max = arr[0];
    for (int i = 1; i < size; i++) {
        if (arr[i] < *min) *min = arr[i];
        if (arr[i] > *max) *max = arr[i];
    }
}

int main() {
    int arr[] = {5, 2, 9, 1, 7};
    int min, max;
    findMinMax(arr, 5, &min, &max);
    printf("Min: %d, Max: %d", min, max);
    return 0;
}

