    int a = 10, b = 20;

    const int *ptr1 = &a;    // Pointer to const int
    int * const ptr2 = &a;   // Const pointer to int

    // ptr1 points to const: can't modify value, can change pointer
    ptr1 = &b;
    printf("ptr1 points to: %d\n", *ptr1);

    // ptr2 is const: can modify value, can't change pointer
    *ptr2 = 15;
    printf("a is now: %d\n", a);

    return 0;
