#include <stdio.h>

void myStrcpy(char *dest, const char *src) {
    while (*src != '\0') {
        *dest = *src;
        dest++;
        src++;
    }
    *dest = '\0';
}

int main() {
    char src[] = "Hello World";
    char dest[20];

    myStrcpy(dest, src);
    printf("Copied: %s", dest);
    return 0;
}

