solution:
#include <stdio.h>
#include <string.h>

void genericSwap(void *a, void *b, size_t size) {
    char temp[size];
    memcpy(temp, a, size);
    memcpy(a, b, size);
    memcpy(b, temp, size);
}

int main() {
    int x = 5, y = 10;
    double d1 = 3.14, d2 = 2.71;

    printf("Before: x=%d, y=%d\n", x, y);
