    printf("After: x=%d, y=%d\n", x, y);

    printf("Before: d1=%.2f, d2=%.2f\n", d1, d2);
    genericSwap(&d1, &d2, sizeof(double));
    printf("After: d1=%.2f, d2=%.2f", d1, d2);

