def get_first_element(arr):
    if not arr:
        return None
    return arr[0]

def sum_array(arr):
    total = 0
    for x in arr:
        total += x
    return total

def contains(arr, target):
    for x in arr:
        if x == target:
            return True
    return False

def count_pairs(arr):
    count = 0
    n = len(arr)
    for i in range(n):
        for j in range(i + 1, n):
            count += 1
    return count

