def add_matrices(mat1, mat2):
    n = len(mat1)
    result = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            result[i][j] = mat1[i][j] + mat2[i][j]
    return result

def steps_to_one(n):
    count = 0
    while n > 1:
        n = n // 2
        count += 1
    return count

def has_duplicates_naive(arr):
    for i in range(len(arr)):
        for j in range(i + 1, len(arr)):
            if arr[i] == arr[j]:
                return True
    return False

def has_duplicates_optimized(arr):
    seen = set()
    for x in arr:
        if x in seen:
            return True
        seen.add(x)
    return False

def factorial(n):
    if n == 0: return 1
    result = 1
    for i in range(1, n + 1):
        result *= i
    return result

