class ThreadManager:
    def __init__(self):
        self.threads = {}
        self.next_id = 1

    def create_thread(self, name):
        tid = self.next_id
        self.threads[tid] = name
        self.next_id += 1
        return tid

    def get_thread_count(self):
        return len(self.threads)

    def get_thread_name(self, thread_id):
        return self.threads.get(thread_id)

