def is_thread_safe(uses_globals, uses_static, uses_locks, is_pure):
    # Pure functions are always thread-safe
    if is_pure:
        return True
    # Functions with globals/statics need locks
    if uses_globals or uses_static:
        return uses_locks
    # No shared state = thread-safe
    return True

