class UserThreadScheduler:
    def __init__(self):
        self.threads = []
        self.current_idx = 0

    def add_thread(self, tid):
        self.threads.append(tid)

    def get_next(self):
        if not self.threads:
            return None
        return self.threads[self.current_idx]

    def yield_thread(self):
        if self.threads:
            self.current_idx = (self.current_idx + 1) % len(self.threads)

