class Thread:
    def __init__(self, tid):
        self.tid = tid
        self.state = "created"

    def start(self):
        if self.state == "created":
            self.state = "running"
            return True
        return False

    def block(self):
        if self.state == "running":
            self.state = "blocked"
            return True
        return False

    def unblock(self):
        if self.state == "blocked":
            self.state = "running"
            return True
        return False

    def terminate(self):
        if self.state in ("running", "blocked"):
            self.state = "terminated"
            return True
        return False

