def tokenize(expr):
    tokens = []
    i = 0
    while i < len(expr):
        if expr[i].isspace():
            i += 1
        elif expr[i].isdigit():
            j = i
            while j < len(expr) and expr[j].isdigit():
                j += 1
            tokens.append(('NUMBER', expr[i:j]))
            i = j
        elif expr[i] == '+':
            tokens.append(('PLUS', '+'))
            i += 1
        elif expr[i] == '-':
            tokens.append(('MINUS', '-'))
            i += 1
        elif expr[i] == '*':
            tokens.append(('TIMES', '*'))
            i += 1
        elif expr[i] == '/':
            tokens.append(('DIVIDE', '/'))
            i += 1
        elif expr[i] == '(':
            tokens.append(('LPAREN', '('))
            i += 1
        elif expr[i] == ')':
            tokens.append(('RPAREN', ')'))
            i += 1
        else:
            raise ValueError(f"Unknown character: {expr[i]}")
    return tokens

