solution:
function onBreakpointChange(query, callback) {
  const mediaQuery = window.matchMedia(query);
  const handler = (e) => callback(e.matches);

  mediaQuery.addEventListener('change', handler);
  callback(mediaQuery.matches); // Call immediately with current state

  return () => mediaQuery.removeEventListener('change', handler);
}

function isKeyboardNavigation() {
  let usingKeyboard = false;

  document.addEventListener('keydown', (e) => {
    if (e.key === 'Tab') {
      usingKeyboard = true;
    }
  });

  document.addEventListener('mousedown', () => {
    usingKeyboard = false;
  });

  return () => usingKeyboard;
}

function getGridColumns(viewportWidth) {
  if (viewportWidth < 768) return 1;
  if (viewportWidth < 1024) return 2;
  return 4;
}

function isTouchDevice() {
  return (
    'ontouchstart' in window ||
    navigator.maxTouchPoints > 0 ||
    navigator.msMaxTouchPoints > 0
  );
}

function hasValidLabel(inputElement) {
  // Check if input is wrapped in a label
  if (inputElement.closest('label')) {
    return true;
  }

  // Check if input has an id and a label with matching for attribute
  const inputId = inputElement.id;
  if (inputId) {
    const label = document.querySelector(`label[for="${inputId}"]`);
    if (label) {
      return true;
    }
  }

  // Check for aria-label or aria-labelledby
  if (inputElement.hasAttribute('aria-label') ||
      inputElement.hasAttribute('aria-labelledby')) {
    return true;
  }

  return false;
}

