solution:
function generateTypeScale(baseSize, ratio, levels) {
  const scale = [];
  for (let i = 0; i < levels; i++) {
    const size = baseSize * Math.pow(ratio, i);
    scale.push(Math.round(size * 100) / 100); // Round to 2 decimals
  }
  return scale;
}
