function delay(ms) {
  return new Promise(resolve => {
    setTimeout(resolve, ms);
  });
}

function randomPromise() {
  return new Promise((resolve, reject) => {
    if (Math.random() > 0.5) {
      resolve('Success!');
    } else {
      reject('Failed!');
    }
  });
}

