  // Your code here
}

solution:
async function fetchWithTimeout(url, timeout) {
  const timeoutPromise = new Promise((_, reject) => {
    setTimeout(() => reject(new Error('Request timeout')), timeout);
  });

  return Promise.race([
    fetch(url).then(res => res.json()),
    timeoutPromise
  ]);
}

