  // Your code here
}

solution:
async function fetchAllPages(baseUrl) {
  const allData = [];
  let url = baseUrl;

  while (url) {
    const response = await fetch(url);
    const page = await response.json();
    allData.push(...page.data);
    url = page.nextPage;
  }

  return allData;
}

