solution:
class AsyncQueue {
  constructor() {
    this.queue = [];
  }

  enqueue(asyncFn) {
    this.queue.push(asyncFn);
  }

  async process() {
    const results = [];
    while (this.queue.length > 0) {
      const task = this.queue.shift();
      try {
        const result = await task();
        results.push({ success: true, result });
      } catch (error) {
        results.push({ success: false, error: error.message });
      }
    }
    return results;
  }
}
