function describe(suiteName, tests) {
  console.log(`\nTest Suite: ${suiteName}`);
  let passed = 0;
  let failed = 0;

  for (const [testName, testFn] of Object.entries(tests)) {
    try {
      testFn();
      console.log(`  ✓ ${testName}`);
      passed++;
    } catch (error) {
      console.log(`  ✗ ${testName}`);
      console.log(`    ${error.message}`);
      failed++;
    }
  }

  console.log(`\nResults: ${passed} passed, ${failed} failed`);
  return { passed, failed };
}

function resolveDependencies(modules) {
  const resolved = [];
  const visited = new Set();

  function visit(module) {
    if (visited.has(module)) return;
    visited.add(module);

    const deps = modules[module] || [];
    for (const dep of deps) {
      visit(dep);
    }

    resolved.push(module);
  }

  for (const module of Object.keys(modules)) {
    visit(module);
  }

  return resolved;
}

function createComponent(config) {
  let isMounted = false;

  return {
    mount() {
      if (!isMounted) {
        isMounted = true;
        if (config.onMount) config.onMount();
      }
    },
    update(newData) {
      if (isMounted && config.onUpdate) {
        config.onUpdate(newData);
      }
    },
    unmount() {
      if (isMounted) {
        isMounted = false;
        if (config.onUnmount) config.onUnmount();
      }
    }
  };
}

function generateBuildConfig(env) {
  const baseConfig = {
    entry: './src/index.js',
    outputPath: './dist'
