  if (env === 'production') {
    return {
      ...baseConfig,
      sourceMap: false,
      minify: true,
      mode: 'production'
    };
  } else {
    return {
      ...baseConfig,
      sourceMap: true,
      minify: false,
      mode: 'development'
    };
  }
}

function createMock(returnValue) {
  const calls = [];

  const mockFn = function(...args) {
    calls.push(args);
    return returnValue;
  };

  mockFn.calls = calls;
  mockFn.calledWith = (...expectedArgs) => {
    return calls.some(args =>
      args.length === expectedArgs.length &&
      args.every((arg, i) => arg === expectedArgs[i])
    );
  };

  return mockFn;
}

function createStore(initialState) {
  let state = initialState;
  const subscribers = [];

  return {
    getState() {
      return state;
    },
    setState(newState) {
      state = { ...state, ...newState };
      subscribers.forEach(fn => fn(state));
    },
    subscribe(fn) {
      subscribers.push(fn);
      return () => {
        const index = subscribers.indexOf(fn);
        if (index > -1) subscribers.splice(index, 1);
      };
    }
  };
}

function createApp() {
  const plugins = [];
  const app = {
    context: {}
  };

  return {
    use(plugin) {
      plugins.push(plugin);
      return this;
    },
    run() {
      plugins.forEach(plugin => {
        plugin(app.context);
      });
      return app.context;
    },
    getContext() {
      return app.context;
    }
  };
}

