  // Your code here
}

solution:
function createTable(data) {
  if (data.length === 0) return '<table></table>';

  const headers = data[0].map(h => `    <th>${h}</th>`).join('\n');
  const rows = data.slice(1).map(row => {
    const cells = row.map(cell => `      <td>${cell}</td>`).join('\n');
    return `  <tr>\n${cells}\n  </tr>`;
  }).join('\n');

  return `<table>
  <thead>
    <tr>
${headers}
    </tr>
  </thead>
  <tbody>
${rows}
  </tbody>
</table>`;
}

