function isSatisfied(lit: number, assignment: Record<number, boolean>): boolean {
  const v = Math.abs(lit);
  const val = assignment[v] ?? false;
  return lit > 0 ? val : !val;
}

const clause = [1, -2, 3];
const a = { 1: false, 2: true, 3: false };
console.log(clause.some(l => isSatisfied(l, a)));
