  name: string;
  params: Param[];
  returnType: string;
  returnDescription: string;
  description: string;
}

function generateJSDoc(info: FunctionInfo): string {
  const lines = ['/**'];
  lines.push(` * ${info.description}`);
  lines.push(' *');

  info.params.forEach(p => {
    lines.push(` * @param {${p.type}} ${p.name} - ${p.description}`);
  });

  lines.push(` * @returns {${info.returnType}} ${info.returnDescription}`);
  lines.push(' */');

  return lines.join('
');
}

