interface Changelog {
  features: string[];
  fixes: string[];
  others: string[];
}

function parseChangelog(commits: string[]): Changelog {
  const changelog: Changelog = { features: [], fixes: [], others: [] };

  commits.forEach(msg => {
    if (msg.startsWith('feat:')) {
      changelog.features.push(msg.substring(5).trim());
    } else if (msg.startsWith('fix:')) {
      changelog.fixes.push(msg.substring(4).trim());
    } else {
      changelog.others.push(msg);
    }
  });

  return changelog;
}

