type ReleaseType = 'major' | 'minor' | 'patch';

function incrementVersion(version: string, type: ReleaseType): string {
  const parts = version.split('.').map(Number);
  let [major, minor, patch] = parts;

  switch (type) {
    case 'major':
      major++;
      minor = 0;
      patch = 0;
      break;
    case 'minor':
      minor++;
      patch = 0;
      break;
    case 'patch':
      patch++;
      break;
  }

  return `${major}.${minor}.${patch}`;
}

