function convertToHTML(text: string): string {
  const lines = text.split('
');
  let html = '';
  let inList = false;

  lines.forEach(line => {
    if (line.startsWith('# ')) {
      if (inList) { html += '</ul>
'; inList = false; }
      html += `<h1>${line.substring(2)}</h1>
`;
    } else if (line.startsWith('* ')) {
      if (!inList) { html += '<ul>
'; inList = true; }
      html += `  <li>${line.substring(2)}</li>
`;
    } else if (line.trim() !== '') {
      if (inList) { html += '</ul>
'; inList = false; }
      html += `<p>${line}</p>
`;
    }
  });

  if (inList) html += '</ul>
';
  return html.trim();
}

