function analyzeFeedback(feedbacks: Feedback[]): { average: number, positiveCount: number } {
  if (feedbacks.length === 0) return { average: 0, positiveCount: 0 };

  const totalScore = feedbacks.reduce((sum, f) => sum + f.score, 0);
  const average = totalScore / feedbacks.length;

  const positiveCount = feedbacks.filter(f => 
    f.comment.toLowerCase().includes('great') || 
    f.comment.toLowerCase().includes('good')
  ).length;

  return { average, positiveCount };
}

