solution:
interface TaskStat {
  estimated: number;
  actual: number;
}

function calculateStats(tasks: TaskStat[], bugs: number): { accuracy: number, bugDensity: number } {
  if (tasks.length === 0) return { accuracy: 0, bugDensity: 0 };

  const totalEst = tasks.reduce((s, t) => s + t.estimated, 0);
  const totalAct = tasks.reduce((s, t) => s + t.actual, 0);

  const accuracy = totalAct === 0 ? 0 : totalEst / totalAct;
  const bugDensity = bugs / tasks.length;

  return { accuracy, bugDensity };
}

