def classify_extremum():
    """
    Classify the critical point (0,0) for f(x,y) = -x² - y²

    Returns:
        String: "maximum", "minimum", "saddle", or "inconclusive"
    """
    # fxx = -2, fyy = -2, fxy = 0
    fxx = -2
    fyy = -2
    fxy = 0
    D = fxx * fyy - fxy**2

    if D > 0:
        if fxx < 0:
            return "maximum"
        else:
            return "minimum"
    elif D < 0:
        return "saddle"
    else:
        return "inconclusive"
