import numpy as np

def classify_trace_det(tau, delta):
    """
    τ = -2, Δ = 5
    Eigenvalues from λ^2 - τλ + Δ = 0
    Discriminant: τ^2 - 4Δ = 4 - 20 = -16 < 0
    => complex eigenvalues
    τ < 0 => stable spiral
    """
    discriminant = tau**2 - 4*delta

    if delta < 0:
        return 'saddle'
    elif discriminant > 0:
        if tau < 0:
            return 'stable node'
        else:
            return 'unstable node'
    elif discriminant < 0:
        if tau < 0:
            return 'stable spiral'
        elif tau > 0:
            return 'unstable spiral'
        else:
            return 'center'
    else:
        return 'degenerate'
